//React
import { useState } from 'react'

//CSS
import './FormCadastrar.css'

//Component
import FormButtons from './FormButtons/FormButtons'
import FormDescricao from './FormDescricao/FormDescricao'
import FormLocalizacao from './FormLocalizacao/FormLocalizacao'
import FormNome from './FormNome/FormNome'

export default function FormCadastrar() {
    const [addPost, setAddPost] = useState(
        {
            nome: '',
            uf: '',
            cidade: '',
            referencia: '',
            descricao: ''
        })

        const handleAddPost = e =>{
            const { name, value } = e.target
            setAddPost({...addPost, [name]:value})
            console.log(addPost)
        }



    return (
        <form className="cadastrar-content" action="http://localhost:5151/Posts" method="POST">
            <FormNome handleAddPost={handleAddPost} value={addPost.nome}/>
            <FormLocalizacao handleAddPost={handleAddPost} valueUF={addPost.uf} valueCidade={addPost.cidade} valueReferencia={addPost.referencia}/>
            <FormDescricao />
            <FormButtons />
        </form>
    )
}
