//React
import React from 'react';

//CSS
import './PageBusca.css'

//Components
import Buscar from "../Buscar/Buscar";
import Header from "../Header/Header";
import Posts from "../Posts/Posts";

export default class PageBuscar extends React.Component {
    state = {
        allPosts: [],
        searchValue: '',
        bigToLower: false,
    }

    handleChangeSearchValue = e => {
        const { value } = e.target
        this.setState({ searchValue: value })
    }

    handleInversePosts = e => {
        const { value } = e.target

        value === "Crescente" ? 
        this.setState({allPosts : this.allPosts.reverse()}) :
        this.setState({allPosts : this.allPosts.sort()})
    }

    requestAllPost = () => {
        fetch('http://localhost:5151/Posts')
            .then(response => response.json())
            .then(data => {
                console.log(data)
                this.setState({ allPosts: data })
            })
    }

    componentDidMount() {
        this.requestAllPost()
    }

    render() {
        const { allPosts, searchValue, bigToLower } = this.state

        const filtredPosts = !!searchValue ? 
        (
            allPosts.filter(post =>{
                return post.nome.toLowerCase().includes(searchValue.toLowerCase())
            })
        )
        : allPosts


        return (
            <>
                <Header />
                <Buscar
                handleInversePosts={this.handleInversePosts} 
                value={searchValue}
                handleChangeSearchValue={this.handleChangeSearchValue} 
                />
                <Posts allPosts={filtredPosts} />
                {!(filtredPosts  === 0) && <h1 className="notfound">Nenhum resultado foi encontrado...</h1>}
            </>
        )
    }
}
